function setup() {
	
	frameRate(18);
	createCanvas(1280, 720);
	imageMode(CENTER);
	textAlign(CENTER);
	rectMode(CENTER); 
	noStroke();
	video = createCapture(VIDEO);
	video.size(1000,1000);
	video.hide(); 
	background(0);
	

	
	//instaces on page 1
	obs1 = new obstacle(lotus,700,600,120,120);
	obs2 = new obstacle(lotus,400,350,70,70);
	obs3 = new obstacle(lotus,820,200,50,50);
	exit1 = new exit(645,75,60,60);
	
  //instances on page 2
	heart = new obstacle(heartimg,width/2,300,80,80);
	exit2 = new exit(width/2, 620,130,130);
	for(let i = 0; i<100; i++){
    stars[i] = new star(random(width),random(height),random(-PI,PI),random(0.1,2));

  }
	//instances on page 3
	exit3 = new exit(100,80,80,80);
	
	//instances on page 4
	puddle = new obstacle(pool,1000,550,300,300);
	exit4 = new exit(1150,100,200,200);

	//instances on page 5
	exit5 = new exit(1070,620,384,200);

	//instances on page 6
	shark1 = new obstacle(shark1img,400,500,300,300);
	shark2 = new obstacle(shark2img,900,500,300,300);
	exit6 = new exit(30,23,94,46);

	//instances on page 7
	exit7 = new exit(1100,600,100,100);

	//instances on page 8
	someone = new obstacle(ghostback,width/2,600,67,80);
	exit8 = new exit(120,150,250,250);



}



function draw() {
	//function for faster access to pages when testing
	testing();
	
	imgcontrol();	
	
	//sounds();
	//call usercollide function
	userCollide();
	if (page == 0){
		start();
		if (keyIsDown(ENTER)){
			if (music.isPlaying() == false){
			music.setVolume(0.3);
			music.play();
			music.loop();
		   }
		}
	}
	//if page is one, display instances on page one, "lotus and buddha"
	if (page == 1){
		image(ghost,xpos,ypos,80,80);
		obs1.display();
		obs2.display();
		obs3.display();
		exit1.display();
		if (box !=false){
			fill(0);
			rect(width/2,height/2,600,200);
		}	
		//text appears depends on which object player collides with
		if (text01 == true){
		//calling type function which creates a character by character typing effect
			type('The natural disasters, meteorites,\n\nUFOs, nuclear explosions, doomsday,\n\nsolar storms, wars, and diseases\n\nthat I bought before Christmas...',width/2,310);
		}
		if (text02 == true){
			type('I will call them to delay\n\nthe delivery...\n\nMy heart is beating,\n\nthe entire world has changed...',width/2,310);
		}
		if (text03 == true){
			type('because of your appearance.',width/2,height/2);
		}
	}


	//displays objects on page 2, "heartbreak"
	if (page == 2){
		image(ghost,xpos,ypos,80,80);
		heart.display();
		exit2.display();
		if (box !=false){
			fill(0);
			rect(width/2,height/2,800,200);
		}	
		if (text201 == true){
			type("Later, I found that you belong to the gazes\n\nof every eye...So I love you more; and then I\n\nfound that no one could ever possess you,\n\nso I love you no more.",width/2, height/2-45);
		}
	}
	
	//displays objects on page 3, "floating stars"
	if (page == 3){
		image(darkghost,xpos,ypos,80,80);
		for (let i = 0; i < stars.length; i++){
    stars[i].display();
		stars[i].move();
  	}
		exit3.display();
		exit3.move3();
		fill(0);
		rect(width/2,height/2,700,150);
		type("I loved you for so long without a reason!\n\nStars...",width/2,height/2-10)
	}
	
	//displays objects on page 4ï¼Œ"human shaped pool"
    if (page == 4){
		puddle.display();
		exit4.display();
		image(umbrella,xpos,ypos,80,80);
		if (box !=false){
			fill(0);
			rect(width/2,height/2,800,200);
		}	
		if (text401 == true){
			type("It would be a bit scary if it really\n\nwere a pool of clear water.\n\nBut the world is so crowded...\n\nmother.",width/2, height/2-48);
		}
	}

	//displays objects on page 5, "ladder to home"

    if (page == 5){
		exit5.display();
		image(flowerr,120,50,198,254.5);
		//image(flowerl,1200,50);
		image(ladder,1100,100);
		image(flower,width/2-40,580);
		image(flower,50,820);
    	image(ghost,xpos,ypos,80,80);
		image(flowerl,1200,50);
		fill(0);
		rect(width/2,height/2,600,150);
		type("The road to home...\n\nThe road to home...",width/2,height/2-10)
    }
    
	//displays objects on page 6, "shark museum"
    if (page == 6){
		shark1.display();
		shark2.display();
		exit6.display();
		image(jayden,915,410,210,65)
		push();
		//translate(width / 2, height / 2);
		rotate(PI / 3.5);
		image(model,640,-50,83.3,207.7)
		pop();
		image(ghost,xpos,ypos,80,80);
		if (box !=false){
			fill(0);
			rect(width/2,height/2,600,150);
		}
		if (text602 == true){
			type("You want me to turn back.\n\nIt is my choice;",width/2,height/2-10)
		}
		if (text601 == true){
			type("You are shouting by the sea,\n\ndrowning me\n\nover and over again.",width/2,height/2-30)
		}
	}
	
	//displays objects on page 7, "hide and seek"
	if (page == 7){
		exit7.display();
		image(ghost,xpos,ypos,80,80);
		image(page7,width/2,height/2,1280,720);
	}


	//displays objects on page 8, "moony"
	if (page==8){
		image(earth,width/2,height/2,1280,720);
		image(moony,width/2,-50,1200,1200);
		image(e8,220,10,150,150)
		someone.display();
		exit8.display();
		image(ghost,xpos,ypos,80,80);
		if (box !=false){
			fill(0);
			rect(width/2,height/2,600,150);
		}
		if (text801 == true){
			type("Die for us! Flying away;\n\nliving until\n\nthere is nothing left",width/2,height/2-30)
		}
	}


	//displays effects on page 9, "face your reality"
	if (page == 9){
		for (let i = 0; i < video.height/60; i++) {
			drawStreak()
		}
		fill(0);
		rect(width/10-30,height/2,200,height);
		rect(width-100,height/2,200,height);
		rect(width/2,height/2,600,150);
		type("...once awakened,\n\nit is but\n\nan illusion that vanishes.",width/2,height/2-30);
		countdown();
		//console.log(timer);
	}

	//credits/ending page
	if (page == 10){
		background(0);
		type("intentional unintended poetry\n\nby phoebe yin",width/2,height/2);
		timer = 10;
		
	}
	console.log(page)
}